import { Component } from '@angular/core';

@Component({
  	selector: 'app-heroes-hero',
  	templateUrl: './hero.component.html',
  	styleUrls: ['./hero.component.css']
})
export class HeroComponent {
	public name: 			string = 'ironman';
	public age:				number = 45;
	public seCambioNombre:	boolean = false;
	public seCambiaEdad:	boolean = false;

	get capitalizedName(): string {
		return this.name.toUpperCase();
	}

	getHeroDescription(): string {
		return `${ this.name } - ${ this.age }`

	}

	changeHero(): void{
		//throw 'Método pendiente de realizar';
		this.seCambioNombre = true;
		this.name = 'Spiderman';
	}

	changeAge(): void{
		this.seCambiaEdad = true;
		this.age = 30;
	}

	resetForm(): void {
		this.name = 'ironman';
		this.age = 45;
		this.seCambiaEdad = false;
		this.seCambioNombre = false;
	}

}
<h1>{{ name }}</h1>

<dl>
	<td>Nombre:</td>
	<dd>{{ name }}</dd>

	<td>Edad:</td>
	<dd>{{ age }}</dd>

	<td>Método:</td>
	<dd>{{ getHeroDescription() }}</dd>

	<td>Capitalizado:</td>
	<dd>{{ capitalizedName }}</dd>
</dl>

<button
	*ngIf="!seCambioNombre"
	class="btn btn-primary mx-2"
	(click)="changeHero()">
	Cambiar nombre
</button>
<button
	*ngIf="!seCambiaEdad"
	class="btn btn-primary"
	(click)="changeAge()">
	Cambiar edad
</button>
<button
	*ngIf="seCambiaEdad || seCambioNombre"
	class="btn btn-primary"
	(click)="resetForm()">
	Reset
</button>
