import { Component } from '@angular/core';

@Component({
  	selector: 'app-heroes-list',
  	templateUrl: './list.component.html',
  	styleUrls: ['./list.component.css']
})
export class ListComponent {

	public heroNames: string[] = ['Spiderman', 'Ironman', 'Hulk', 'She Hulk', 'Thor'];
	public borrado: string | undefined;

	borrarHeroe = ():void =>{
		this.borrado = this.heroNames.pop();
	}

}
<p>Listado de Héros</p>

<div *ngIf="borrado; else nothingWasDeleted">
	<h3>Héroe borrado <small class="text-danger"> {{ borrado }} </small></h3>
</div>
<ng-template #nothingWasDeleted>
	<h3>No ha borrado nada</h3>
</ng-template>


<button class="btn btn-primary" (click)="borrarHeroe()" *ngIf="heroNames.length > 0">
	Borrar último Héroe
</button>

<ul class="mt-2 list-group">
	<li class="list-group-item" *ngFor="let name of heroNames;">
		<strong>{{ name }}</strong>
	</li>
</ul>
