  	selector: 'app-root',
  	templateUrl: './app.component.html',
  	styleUrls: ['./app.component.css']
})
export class AppComponent {
	public title: string = 'Hola mundo';
}
<h1>{{ title }}</h1>
<hr>

<app-heroes-hero></app-heroes-hero>

<app-heroes-list></app-heroes-list>
